export default {
  name: 'RotatorTools',
  data() {
    return {
      azimut: 0,
      elevation: 0
    };
  },
  methods: {
    zeroaxes() {
      this.$ajax.post('/api/v1/data/set/nullX', {
        key: localStorage.getItem('rotator_client_id')
      });
    },
    sendangles() {
      this.$ajax.post('/api/v1/data/set/currentangles', {
        key: localStorage.getItem('rotator_client_id'),
        azimut: Number(this.azimut),
        elevation: this.elevation
      });
    }
  }
};
