import { bus } from '@/event-bus';
export default {
  name: 'RotatorMain',
  data() {
    return {
      azimut: 0,
      elevation: 0,
      battery: 0,
      ws: null
    };
  },
  created() {
    this.ws = new WebSocket(`ws://${window.location.hostname}/ws`); // `ws://localhost:2000/`
    this.ws.onopen = function () {
      // eslint-disable-next-line
      console.log('WS подключен');
    };
    this.ws.onclose = function (eventclose) {
      // eslint-disable-next-line
      console.log('Cоеденение закрыто: ');
      // eslint-disable-next-line
      console.log(eventclose);
    };
    this.ws.onmessage = function (msg) {
      var response = JSON.parse(msg.data);
      this.battery = response.voltage.toFixed(2);
      this.azimut = response.azimut.toFixed(2);
      this.elevation = response.elevation.toFixed(2);
    }.bind(this);
    bus.$on('send_ws', mess => {
      this.ws.send(mess);
    });
