import { bus } from '@/event-bus';
export default {
  name: 'RotatorAuth',
  data() {
    return {
      login_loading: false,
      is_admin: 0,
      // process.env.NODE_ENV === 'development',
      login: null,
      password: null
    };
  },
  watch: {
    is_admin(val) {
      bus.$emit('is_admin', val ? 1 : 0);
    }
  },
  methods: {
    send() {
      this.login_loading = true;
      // eslint-disable-next-line
      //console.log(this.color_data);
      this.$ajax.post('/api/v1/users/auth', {
        login: this.login,
        password: this.password,
        key: localStorage.getItem('rotator_client_id')
      }).then(data => {
        // eslint-disable-next-line
        console.log(data);
        if (data.data.role === 'admin') {
          this.is_admin = 1;
          bus.$emit('close_dialogs');
        }
        this.login_loading = false;
      }).catch(error => {
        // eslint-disable-next-line
        console.log(error);
      });
      //setTimeout(() => { this.checkadmin() }, 2000)
    },

    checkadmin() {
      if (localStorage.getItem('rotator_client_id') != null) {
        this.$ajax.post('/api/v1/users/whoami', {
          key: localStorage.getItem('rotator_client_id')
        }).then(data => {
          // eslint-disable-next-line
          console.log(data);
          alert(data.data.role);
          if (data.data.role == "admin") {
            alert('admin');
            bus.$emit('is_admin', 1);
            this.is_admin = 1;
            bus.$emit('close_dialogs');
          }
        }).catch(error => {
          // eslint-disable-next-line
          console.log(error);
        });
      }
    }
  },
  created() {
    this.checkadmin();
    bus.$emit('is_admin', this.is_admin ? 1 : 0);
    bus.$on('get_admin', () => {
      bus.$emit('is_admin', this.is_admin ? 1 : 0);
    });
  }
};
